mapper.select:
/* getRatioUsingEnrollId */
	SELECT ROUND(complete_hours/hours * 100) 
	FROM ENROLL e, SUBJECT s 
	WHERE e.subject_id = s.subject_id AND enroll_id = #{enrollId} 
	AND NOT (e.del_yn ='Y')

/* getStudentList */ SELECT student_id, name, gender_cd, TO_CHAR(TO_DATE(birth, 'YYYYMMDD'), 'YYYY-MM-DD') AS "birth", email, REGEXP_REPLACE(phone, '(.{3})(.+)(.{4})', '\1-\2-\3') AS "phone", add_do_cd, add_etc, position_cd FROM STUDENT WHERE
name = #{keyword}
UPPER(student_id) = UPPER(#{keyword})

/* getOpenList */
SELECT o.subject_id, subject_title, o.course_id, course_title, start_day, end_day, start_time, end_time, recruit_start_day, recruit_end_day, recruit_people FROM OPEN o LEFT OUTER JOIN SUBJECT s ON o.subject_id = s.subject_id LEFT OUTER JOIN COURSE c ON o.course_id = c.course_id WHERE o.course_id is NULL AND NOT(o.state_cd ='OPN07')
SELECT o.course_id, course_title, min(start_day) AS "startDay", max(end_day) AS "endDay", min(recruit_start_day) AS "recruitStartDay", max(recruit_end_day) AS "recruitEndDay", max(recruit_people) AS "recruitPeople" FROM OPEN o LEFT OUTER JOIN COURSE c ON o.course_id = c.course_id GROUP BY (o.course_id, course_title) HAVING (UPPER(o.course_id) = UPPER(#{kw}) OR REPLACE(course_title, ' ', '') LIKE '%' || #{kw} || '%')

/* getSubjectCountByCourse */
	SELECT count(subject_id)
	FROM open
	WHERE course_id=#{courseId}

/* getSubjectInfoByCourse */
	select 
	    subject_id, 
	    subject_seq 
	from open
	where course_id = #{courseId}

/* recruitTotalPeople */ select count(*) from enroll where subject_id=#{subjectId} and subject_seq=#{subjectSeq}
and state_cd = 'ERL01'
and state_cd = 'ERL03'
and state_cd = 'ERL04'
and state_cd = 'ERL06'

mapper.update:
/* clickCancel */
	UPDATE ENROLL 
	SET state_cd = 'ERL05' 
	WHERE student_id = #{studentId} AND subject_id = #{subjectId} AND subject_seq = #{subjectSeq}

/* clickDelete */
	UPDATE ENROLL 
	SET del_yn = 'Y' 
	WHERE student_id = #{studentId} AND subject_id = #{subjectId} AND subject_seq = #{subjectSeq}

/* clickDeleteEnrollByOpen */
	UPDATE ENROLL 
	SET del_yn = 'Y' 
	WHERE subject_id = #{subjectId} AND subject_seq = #{subjectSeq}

/* addHours */
	UPDATE ENROLL 
	SET complete_hours = complete_hours + #{addHours} 
	WHERE enroll_id = #{enrollId}

/* updateCancelRsCd */
	UPDATE ENROLL 
	SET cancel_rs_cd = #{cancelRsCd} 
	WHERE student_id = #{studentId} AND subject_id = #{subjectId} AND subject_seq = #{subjectSeq}

/* updateCancelRsCd */
	UPDATE ENROLL 
	SET cancel_rs_cd = #{cancelRsCd}, cancel_rs_etc = #{cancelRsEtc} 
	WHERE student_id = #{studentId} AND subject_id = #{subjectId} AND subject_seq = #{subjectSeq}

/* approval */
	UPDATE ENROLL 
	SET state_cd = 'ERL03' 
	WHERE student_id = #{studentId} AND subject_id = #{subjectId} AND subject_seq = #{subjectSeq}
