mapper.select:
/* getComnCdTitle */
	SELECT comn_cd_title
	FROM common_code
	WHERE comn_cd=#{comnCd}

/*searchSubject*/ SELECT subject_id, subject_title, support_yn, cat_cd as cat_subject, days, hours, level_cd as "level", level_etc, cost, reg_year FROM subject
AND subject_id = #{subjectId}
AND (subject_title = #{subjectTitle} OR REPLACE (subject_title, ' ', '') LIKE '%'||#{subjectTitle}||'%')
AND cat_cd = #{catSubject}
AND level_cd = #{level}
AND reg_year = #{regYear}

/*searchCourse*/
	SELECT
	    course_id,
	    course_title,
	    cat_cd as cat_course_cd
	FROM course


			AND course_id = #{courseId}


			AND (course_title = #{courseTitle} OR REPLACE (course_title, ' ', '') LIKE '%'||#{courseTitle}||'%')


			AND cat_cd = #{catCourseCd}

/*searchOpenSubject*/ SELECT open.subject_id, subject_seq, subject_title, support_yn, days, hours, level_cd as "level", level_etc, subject.cost, open.course_id, course.course_title, start_time, end_time, start_day, end_day, recruit_start_day, recruit_end_day, recruit_people, state_cd as state, comn_cd_title, file_id, subject.cat_cd as cat_subject, subject.reg_year, reg_dt FROM open INNER JOIN subject ON open.subject_id = subject.subject_id LEFT JOIN course ON open.course_id = course.course_id LEFT JOIN common_code ON open.state_cd = common_code.comn_cd WHERE del_yn = 'N'
AND subject.subject_id = #{subjectId}
AND (subject_title = #{subjectTitle} OR REPLACE (subject_title, ' ', '') LIKE '%'||#{subjectTitle}||'%')
AND subject.cat_cd = #{catSubject}
AND level_cd = #{level}
AND subject.reg_year = #{regYear}
AND state_cd = #{state}
ORDER BY start_day, end_day

/*searchOpenCourse*/ select DISTINCT open.course_id, course_title, TO_CHAR(TO_DATE(min(start_day) over (partition by open.course_id)), 'YYYY-MM-DD') as start_day, TO_CHAR(TO_DATE(max(end_day) over (partition by open.course_id)), 'YYYY-MM-DD') as end_day, sum(cost) over (partition by open.course_id) as cost, TO_CHAR(TO_DATE(recruit_start_day), 'YYYY-MM-DD') as recruit_start_day, TO_CHAR(TO_DATE(recruit_end_day), 'YYYY-MM-DD') as recruit_end_day, course.cat_cd as cat_course_cd, subject.reg_year from open left join subject on open.subject_id = subject.subject_id left join course on open.course_id = course.course_id left join common_code on open.state_cd = common_code.comn_cd where open.course_id IS NOT NULL AND del_yn = 'N' and open.state_cd in ('OPN02','OPN03','OPN04')
AND open.course_id = #{courseId}
AND (course_title = #{courseTitle} OR REPLACE (course_title, ' ', '') LIKE '%'||#{courseTitle}||'%')
AND course.cat_cd = #{catCourseCd}
AND subject.reg_year = #{regYear}
AND open.state_cd = #{stateCd}
