mapper.select:
/*getCountOpenCourseRow*/
SELECT count(distinct open.course_id)
FROM open
LEFT JOIN course ON open.course_id = course.course_id
WHERE open.course_id is not null AND del_yn = 'N'

	AND course.cat_cd = #{catCourse}

/*selectOpenCourseListByPage*/
select DISTINCT open.course_id, course_title, min(start_day) over (partition by open.course_id) as start_day, max(end_day) over (partition by open.course_id) as end_day, sum(cost) over (partition by open.course_id) as cost, recruit_start_day, recruit_end_day, state_cd as state, comn_cd_title, course.cat_cd as cat_course from open left join subject on open.subject_id = subject.subject_id left join course on open.course_id = course.course_id left join common_code on open.state_cd = common_code.comn_cd where open.course_id IS NOT NULL AND del_yn = 'N'
AND course.cat_cd = #{catCourse}

/*getCountOpenSubjectRow*/
SELECT count(*)
FROM open
LEFT JOIN subject ON open.subject_id = subject.subject_id
WHERE del_yn = 'N' 

	AND subject.cat_cd=#{catSubject}

/*selectOpenSubjectListByPage1*/
select open.subject_id, subject_seq, subject_title, support_yn, days, hours, level_cd as "level", level_etc, subject.cost, open.course_id, course.course_title, start_time, end_time, start_day, end_day, recruit_start_day, recruit_end_day, recruit_people, state_cd as state, comn_cd_title, file_id, subject.cat_cd as cat_subject, open.reg_dt from open left join subject on open.subject_id = subject.subject_id left join course on open.course_id = course.course_id left join common_code on open.state_cd = common_code.comn_cd WHERE del_yn = 'N'
AND subject.cat_cd=#{catSubject}
order by open.reg_dt desc

/*selectSearchListByPage*/
SELECT enroll_id, e.subject_id, e.subject_seq, s.subject_title, c.course_id, c.course_title, e.student_id, name, e.state_cd, complete_hours, e.reg_id, e.reg_dt, e.modi_id, e.modi_dt, e.del_yn, cancel_rs_etc, start_time, end_time, start_day, end_day, o.state_cd as open_state_cd FROM ENROLL e LEFT OUTER JOIN STUDENT t ON e.student_id = t.student_id LEFT OUTER JOIN OPEN o ON e.subject_id = o.subject_id AND e.subject_seq = o.subject_seq LEFT OUTER JOIN SUBJECT s ON e.subject_id = s.subject_id LEFT OUTER JOIN COURSE c ON c.course_id = o.course_id WHERE NOT (e.del_yn ='Y')
ORDER BY e.reg_dt DESC
