mapper.update:
		update open
		set 
    		start_day =#{startDay}, end_day=#{endDay}, start_time = #{startTime}, end_time = #{endTime}, 
    		recruit_start_day=#{recruitStartDay} , recruit_end_day = #{recruitEndDay}, recruit_people = #{recruitPeople},
    		content = #{content}, state_cd = #{state}, file_id=#{fileId}
		where subject_id = #{subjectId} and subject_seq = #{subjectSeq}

update upload_file
set
	file_name=#{fileName}, file_size=#{fileSize}, 
	file_content_type=#{fileContentType}, file_data=#{fileData}
where 
	file_id=#{fileId}

update open
set recruit_start_day=#{recruitStartDay} , recruit_end_day=#{recruitEndDay} ,state_cd=#{state}
where course_id = #{courseId}

update open
set del_yn = 'Y'
where subject_id = #{subjectId} and subject_seq = #{subjectSeq}

update upload_file
set del_yn = 'Y'
where file_id = #{fileId}

update open 
set state_cd = 'OPN07' 
where subject_id = #{subjectId} and subject_seq = #{subjectSeq}

mapper.select:
select subject_id as "subjectId"
from open 
where course_id = #{courseId}
