mapper.insert:
/*insertSubject*/
insert into open (subject_id, subject_seq, course_id, file_id, content, start_time, end_time, start_day, end_day, recruit_start_day, recruit_end_day, recruit_people, state_cd, reg_id, reg_dt, del_yn) values (#{subjectId},SEQ_SUBJECT.NEXTVAL,#{courseId}, #{fileId}, #{content}, TO_CHAR(TO_TIMESTAMP(#{startTime},'HH24:MI'),'HH24MI'), TO_CHAR(TO_TIMESTAMP(#{endTime},'HH24:MI'),'HH24MI'), TO_CHAR(TO_DATE(#{startDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{endDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{recruitStartDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{recruitEndDay},'YYYY-MM-DD'), 'YYYYMMDD'), #{recruitPeople},#{state}, 'mgr1', TO_CHAR(SYSDATE, 'YYYYMMDD'), 'N')
insert into open (subject_id, subject_seq, course_id, content, start_time, end_time, start_day, end_day, recruit_start_day, recruit_end_day, recruit_people, state_cd, reg_id, reg_dt, del_yn) values (#{subjectId},SEQ_SUBJECT.NEXTVAL,#{courseId}, #{content}, TO_CHAR(TO_TIMESTAMP(#{startTime},'HH24:MI'),'HH24MI'), TO_CHAR(TO_TIMESTAMP(#{endTime},'HH24:MI'),'HH24MI'), TO_CHAR(TO_DATE(#{startDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{endDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{recruitStartDay},'YYYY-MM-DD'), 'YYYYMMDD'), TO_CHAR(TO_DATE(#{recruitEndDay},'YYYY-MM-DD'), 'YYYYMMDD'), #{recruitPeople},#{state}, 'mgr1', TO_CHAR(SYSDATE, 'YYYYMMDD'), 'N')

mapper.update:
/*clickDeleteOpen*/
update open
set del_yn = 'Y'
where subject_id = #{subjectId} and subject_seq = #{subjectSeq}

/*clickDeleteUploadFile*/
update upload_file
set del_yn = 'Y'
where file_id = #{fileId}

/*closeSubject*/
update open 
set state_cd = 'OPN07' 
where subject_id = #{subjectId} and subject_seq = #{subjectSeq}

mapper.select:
/*selectOpenSubjectByCourseIdAndYear*/
	SELECT *
	FROM open
	WHERE course_id = #{courseId}
	AND substr(reg_dt,1,4) = #{year}
	AND del_yn = 'N' AND state_cd != 'OPN07'
